"use strict";function updateTimer(deadline){"use strict"; var time=deadline - new Date(); return{'days' : Math.floor(time/(1000*60*60*24)), 'hours' : Math.floor((time/(1000*60*60)) % 24), 'minutes' : Math.floor((time/1000/60) % 60), 'seconds' : Math.floor((time/1000) % 60), 'total' : time};}function startTimer(id, deadline){"use strict"; var timerInterval=setInterval(function(){var clock=document.getElementById(id); var timer=updateTimer(deadline); clock.innerHTML='<li>' + timer.days + '</li>' + '<li>' + timer.hours + '</li>' + '<li>' + timer.minutes + '</li>' + '<li>' + timer.seconds + '</li>'; if (timer.total < 1){clearInterval(timerInterval); clock.innerHTML='<li>' + 0 + '</li>' + '<li>' + 0 + '</li>' + '<li>' + 0 + '</li>' + '<li>' + 0 + '</li>';}}, 1000);}
